package com.zoho.secondround;

public class ArrayRotations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		  Output: 5671234

		 */
		int arr[] = {3, 4, 5, 6, 7, 1, 2};
	     int n=2;
	     
	     for(int i=n;i<arr.length;i++)
	     {
	    	System.out.println(arr[i]); 
	     }
		
	for(int i=0;i<n;i++)
	{
		System.out.println(arr[i]);
	}
	     
	
	}
}
